INSERT INTO `import_schema` VALUES (103,'Cacti Host Profile Sync 2.1.0',';;','This is a starter template to sync Cacti data with a host profile called cacti-host. Therefore you must create a host profile named cacti-host to use it as is. Data in cacti_data.txt has been extracted using extract_cacti.pl. Hosts that are flagged for deletion will be assigned a random record id.','host-profile-sync',NULL,1,NULL,'/usr/local/groundwork/core/monarch/automation/data/cacti_data.txt'),(104,'NeDi Host Import 2.1.0',';;','This is a simple NeDi starter template to import hosts and network devices. It is designed to work with data generated by extract_nedi.pl, and will set parent child relationships.','host-import',NULL,1,1,'/usr/local/groundwork/core/monarch/automation/data/nedi_data.txt'),(105,'NeDi Host Parent Sync 2.1.0',';;','This template will establish parent child relationships from data generated by extract_nedi.pl. It will not import hosts or devices, and will therefore only update existing hosts. In matching the host, it will try the host name first, then the address followed by the the alias.','other-sync','Host',NULL,NULL,'/usr/local/groundwork/core/monarch/automation/data/nedi_data.txt');
